    private void headerMousePressed(java.awt.event.MouseEvent evt) {                                    
        xMouse = evt.getX();
        yMouse = evt.getY();
    }                                   

    private void headerMouseDragged(java.awt.event.MouseEvent evt) {                                    
        int x = evt.getXOnScreen();
        int y = evt.getYOnScreen();       
        this.setLocation(x - xMouse,y - yMouse);
    }                                   

    private void tsairMouseClicked(java.awt.event.MouseEvent evt) {                                   
       System.exit(0);
    }                                  

    private void tsairMouseEntered(java.awt.event.MouseEvent evt) {                                   
        tsair.setBackground(Color.RED);
        bsair.setBackground(Color.RED);      
    }                                  

    private void tsairMouseExited(java.awt.event.MouseEvent evt) {                                  
        tsair.setBackground(Color.WHITE);
        tsair.setBackground(Color.BLACK);
        bsair.setBackground(Color.WHITE);
    }                                 

    private void tusuarioMousePressed(java.awt.event.MouseEvent evt) {                                      
       if(tusuario.getText().equals("Digite seu usuário")){
         tusuario.setText("");
         tusuario.setForeground(Color.BLACK);
        }
       if(String.valueOf(tsenha.getPassword()).isEmpty()){
         tsenha.setText("********");
         tsenha.setForeground(Color.gray);
        }
    }                                     

    private void tsenhaMousePressed(java.awt.event.MouseEvent evt) {                                    
       if(String.valueOf(tsenha.getPassword()).equals("********")){
         tsenha.setText("");
         tsenha.setForeground(Color.BLACK);
        }
       if(tusuario.getText().isEmpty()){
         tusuario.setText("Digite seu usuário");
         tusuario.setForeground(Color.GRAY);
    }
    }                                   

