    private void bloginActionPerformed(java.awt.event.ActionEvent evt) {                                       
     //tusuario tsenha
            try {
            String nome_cliente=tusuario.getText();
            String senha_cliente=tsenha.getText();

            ClienteDTO objclientedto=new ClienteDTO();
            objclientedto.setNome_usuario(nome_cliente);
            objclientedto.setSenha_usuario(senha_cliente);

            ClienteDAO objclientedao=new ClienteDAO();
            ResultSet rclientedao=objclientedao.autenticacaoUsuario(objclientedto);

            if(rclientedao.next()){
               MenuPrincipal mp = new MenuPrincipal();  
               mp.setVisible(true);
               dispose();
            }else{
                JOptionPane.showMessageDialog(null, "Usuário ou senha invalida!");
            }
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Login: " + e);
        }

    }                                      

